<?php if (!defined('THINK_PATH')) exit();?><!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.0 Transitional//EN"><html xmlns="http://www.w3.org/1999/xhtml"><head><title>EasyTalk Administrator's Control Panel</title><meta http-equiv="Content-Type" content="text/html; charset=utf-8"><meta content="H.Joeson" name="Copyright" /><link rel="stylesheet" href="<?php echo __PUBLIC__;?>/admin/style.css" type="text/css" media="all" /><script src="<?php echo __PUBLIC__;?>/js/jquery.js" type="text/javascript"></script><script src="<?php echo __PUBLIC__;?>/admin/admin.js" type="text/javascript"></script></head><body><div id="bodymain"><div class="title"><?php echo ($position); ?></div><div class="content"><?php if(ACTION_NAME=='index'){ ?><h3>搜索用户</h3><form action='<?php echo SITE_URL;?>/admin.php?s=/Users/search' method="POST"><table class="table" style="margin:5px 0 20px 0"><tr><td width="150px">用户帐号</td><td><input type="text" name="user_name" class="txt_input">&nbsp;&nbsp;&nbsp;● 支持模糊搜索</td></tr><tr><td width="150px">用户分组</td><td><select name="group" class="txt_input" style="width:100px"><option value="all">所有用户</option><option value="admin1">总管理员</option><option value="admin2">前台管理</option><option value="public">普通用户</option><?php echo ($sgroup); ?><option value="lock">锁定用户</option><option value="close">禁言用户</option></select></td></tr></table><input type="submit" class="button1 submit" value="搜索用户"></form><?php }else if (ACTION_NAME=='search'){ ?><h3>用户管理</h3><div style="background:#f1f7fd;padding:5px 10px"><b>共搜索到 <?php echo ($count); ?> 名符合条件的用户</b>&nbsp;&nbsp;&nbsp;<a href="<?php echo SITE_URL;?>/admin.php?s=/Users">重新搜索</a></div><table class="table" style="margin:5px 0 20px 0;width:700px"><tr><td width="50px">&nbsp;</td><td width="150px"><b>用户帐号</b></td><td width="150px"><b>用户昵称</b></td><td style="text-indent:0px" width="100px"><b>用户分组</b></td><td width="150px"><b>注册时间</b></td><td width="100px">&nbsp;</td></tr><?php foreach($user as $val){ ?><tr><td><img src="<?php echo sethead($val[user_head]);?>" style="width:30px;height:30px"></td><td><?php echo ($val[user_name]); ?></td><td><?php echo ($val[nickname]); ?></td><td style="text-indent:0px"><?php if ($val[isadmin]==1) { echo '总管理员'; } else if ($val[isadmin]==2) { echo '前台管理员'; } else if ($val[userlock]==1) { echo '锁定用户'; } else if ($val[userlock]==2) { echo '禁言用户'; } else { echo '普通用户'; } if ($val[user_auth]>0) { echo '<br/><font color="red">'.$sgroup2[$val[user_auth]].'</font>'; } ?></td><td><?php echo date('Y-m-d H:i:s',$val['signupdate']);?></td><td><input type="button" class="button1" value="编辑用户" onclick="window.location.href='<?php echo SITE_URL;?>/admin.php?s=/Users/edit/user_name/<?php echo ($val[user_name]); ?>'"></td></tr><?php } ?></table><div class="page"><?php echo ($page); ?></div><?php } if($user && ACTION_NAME=='edit'){ ?><h3>编辑用户&nbsp;&nbsp;&nbsp;<span style="font-size:12px;font-weight:normal"><a href="<?php echo SITE_URL;?>/admin.php?s=/Users">(重新搜索)</a></span></h3><form action='<?php echo SITE_URL;?>/admin.php?s=/Users/edituser' method="POST" onsubmit="if($('#delmsg').val()==1){if(confirm('你是否要确定删除该用户的广播？')){return true;}else{return false;}}"><table class="table" style="margin:5px 0 20px 0"><tr><td width="150px">用户ID</td><td width="330px"><?php echo ($user['user_id']); ?></td><td></td></tr><tr><td>用户帐号</td><td><input type="text" name="user_name" class="readonly" value="<?php echo ($user['user_name']); ?>" readonly='true'></td><td></td></tr><tr><td>用户昵称</td><td><input type="text" name="user[nickname]" class="txt_input" value="<?php echo ($user['nickname']); ?>"></td><td></td></tr><tr><td>用户密码</td><td><input type="text" name="user[password]" class="txt_input" value=""></td><td class="info">● 如果不修改密码请留空</td></tr><tr><td>用户邮箱</td><td><input type="text" name="user[mailadres]" class="txt_input" value="<?php echo ($user['mailadres']); ?>"></td><td></td></tr><tr><td valign="top">认证说明</td><td><textarea type="text" name="user[auth_info]" style="font-size:12px;border:1px solid #cccccc;height:80px;width:300px;padding:1px 3px"><?php echo ($user['auth_info']); ?></textarea></td><td class="info" valign="top">● 用户认证信息，支持HTML</td></tr><tr><td>用户认证</td><td><select name="user[user_auth]"><?php echo ($vgroup); ?></select></td><td class="info" valign="top">● 用户认证分组请<a href="admin.php?s=/Vipgroup">点击这里</a></td></tr><tr><td>管理员组</td><td><?php if($user[user_id]!=ADMIN_UID){ ?><select name="user[isadmin]"><option value="0" <?php if($user['isadmin']==0){ ?>selected<?php } ?>>普通用户</option><option value="1" <?php if($user['isadmin']==1){ ?>selected<?php } ?>>总管理员</option><option value="2" <?php if($user['isadmin']==2){ ?>selected<?php } ?>>前台管理</option></select><?php }else{ ?>
    默认管理员无法修改
    <?php } ?></td><td class="info">● 总管理员拥有所有权限，前台管理员仅拥有网站广播的审核权限</td></tr><tr><td>注册邮件认证</td><td><select name="regmailauth"><option value="0" <?php if($user[regmailauth]==0){ ?>selected<?php } ?>>否</option><option value="1" <?php if($user[regmailauth]==1){ ?>selected<?php } ?>>是</option></select></td><td></td></tr><tr><td>禁止类型</td><td><select name="user[userlock]"><option value="0" <?php if($user['userlock']==0){ ?>selected<?php } ?>>正常状态</option><option value="1" <?php if($user['userlock']==1){ ?>selected<?php } ?>>锁定状态</option><option value="2" <?php if($user['userlock']==2){ ?>selected<?php } ?>>禁言状态</option></select></td><td class="info">● 用户锁定后将不能登陆，其他用户也不可浏览其主页</td></tr><tr><td>删除用户广播</td><td><select id="delmsg" name="delmsg"><option value="0" selected>不删除</option><option value="1">删除</option></select></td><td class="info">● 选择此项提交后将删除用户的所有广播</td></tr></table><input type="submit" class="button1 submit" value="保存提交"></form><h3>基本信息</h3><table class="table" style="margin:5px 0 20px 0"><tr><td width="150px">注册IP</td><td><?php echo $user['regip']?$user['regip']:'暂未IP';?></td><td></td></tr><tr><td>注册时间</td><td><?php echo date('Y-m-d H:i:s',$user['signupdate']);?></td><td></td></tr><tr><td>最后登陆</td><td><?php echo date('Y-m-d H:i:s',$user['last_login']);?></td><td></td></tr><tr><td>广播数</td><td><?php echo ($user['msg_num']); ?></td><td></td></tr><tr><td>收听数</td><td><?php echo ($user['follow_num']); ?></td><td></td></tr><tr><td>听众数</td><td><?php echo ($user['followme_num']); ?></td><td></td></tr></table><?php } ?></div></div></body></html>